import React from 'react';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

import useStyles from './components/styled'
import NavBar from './components/NavBar'
// import Record from './components/RecordDiv'

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import MicIcon from '@material-ui/icons/Mic';

import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core';

const useStyles2 = makeStyles({
  content: {
    position: "relative",
    marginLeft: 40,
    marginRight: 40,
    marginTop: 30,
    height: 340,
  },
})

function ListItemLink({ item }) {
  return (
    <ListItem button style={{marginTop:5}}>
      <ListItemIcon>
        {item['done'] ? <MicIcon color='primary'/> : <MicIcon color='disabled'/>}
      </ListItemIcon>
      <Typography noWrap={true}>{item['text']}</Typography>
    </ListItem>
  );
}

export default function SimpleCard() {
  const classes = useStyles();
  const classes2 = useStyles2();

  const sentList = [
    {text: '섀도우 헌터스 원 뼈의 도시는 \n카산드라 클레어의 소설로,\n 섀도우 헌터스 시리즈의 첫 작품이다.', done : true},
    {text: '네이버 웹툰에서 연재하고 있는 \n상하 작가의 웹툰이며,\n 네이버 수요일 웹툰 이위를 한 \n인기 웹툰이기도 하다.', done : true},
    {text: '저도 얘기만 들었지, \n직접 와보기는 처음이네요,\n 듣던 대로 구경할 게 \n정말 많네요.', done : false},
    {text: '한 사람 더 왔다고 아까 얘기했어요,\n 술잔이랑 앞접시 좀 빨리 주세요.', done : true},
    {text: '술 못 마신다는 얘긴 못 들었는데,\n 왜 못 먹는 거야?', done : false},
    {text: '말 꺼내기 쉽지 않은 걸 보니,\n 한잔하면서 툭 터놓고 얘기할까?', done : true},
    {text: '가산디지털단지 차 막히는 거야 \n하루 이틀 얘긴가요, \n상관없으니까 안전하게 가주세요.', done : false},
    {text: '너무 예쁘다, 고맙다 얘들아.', done : false},
    {text: '템플스테이 다녀온 얘기 좀 해봐요, 재밌었어요?', done : false},
  ]

  return (
    <Card className={classes.card}>
      <NavBar classes={classes} />
      <CardContent className={classes.cardContent}>
        {/* <Record classes={classes}/> */}

        <div className={classes2.content}>
          <List component="nav" aria-label="main mailbox folders">
            {sentList.map((item) => {return <ListItemLink item={item} />})}
          </List>
        </div>
      </CardContent>
    </Card>
  );
}
