import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import AppBar from '@material-ui/core/AppBar';
import Typography from '@material-ui/core/Typography';
import { Hidden, IconButton, Paper, Toolbar } from '@material-ui/core';
import ListIcon from '@material-ui/icons/List';
import SettingsIcon from '@material-ui/icons/Settings';

const useStyles = makeStyles({
  card: {
    // position:"relative",
    margin: "auto",
    width: 600,
    height: 600,
    marginTop: "20vh",
    borderRadius: 10,
    backgroundColor:'#fefdfe'
  },
  appbar: {
    position: 'static',
  },
  toolbar: {
    height: 80,
  },
  title: {
    marginLeft: "auto",
    marginRight: "auto",
  },
  cardContent: {
    padding: 0
  },
  content: {
    position: "relative",
    marginLeft: 40,
    marginRight: 40,
    marginTop: 40,
    height: 340,    
  },
  index: {
    paddingRight: 20,
    paddingTop: 20,
    textAlign: "right",
  },
  parSentence: {
    height: 160,
    // backgroundColor:"#ef928f",
    borderTop :"3px solid",
    borderBottom :"3px solid",
    marginTop:10,
  },
  sentence: {
    position:'relative',
    textAlign:"center",
    width:"100%",
    top:"50%",
    transform:"translateY(-50%)",
  },
  visual: {
    position:'relative',
    textAlign:"center",
    height:100,
    marginTop:10,    
  },
  status:
  {
    position:'relative',
    width:"100%",
    top:"50%",
    transform:"translateY(-50%)",
    // display:'none'
  }

});

export default function SimpleCard() {
  const classes = useStyles();

  return (
    <Card className={classes.card}>
      <AppBar className={classes.appbar} elevation={0} >
        <Toolbar className={classes.toolbar}>
          <IconButton color="inherit" aria-label="List">
            <ListIcon fontSize="large" />
          </IconButton>
          <Typography variant="h4" className={classes.title}>
            Voice Collector
          </Typography>
          <IconButton color="inherit" aria-label="Setting">
            <SettingsIcon fontSize="large" />
          </IconButton>
        </Toolbar>
      </AppBar>
      <CardContent className={classes.cardContent}>
        <div className={classes.content}>
          <div className={classes.index}>
            <Typography variant="h6">015 / 300</Typography>
          </div>
          <div className={classes.parSentence}>
            <div className={classes.sentence}>
              <Typography variant="h4">간장공장 공장장은 장 공장장이고,</Typography> 
              <Typography variant="h4">된장 공장 공장장은 간 공장장이다.</Typography>
            </div>
          </div>
          <div className={classes.visual}>
            <div className={classes.status}>
            <Typography variant="h4">Ready to record</Typography>
            </div>
            <div>

            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}
