import { React, useRef, useEffect, useState } from "react";

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

import useStyles from './components/styled'
import NavBar from './components/NavBar'
import Record from './components/RecordDiv'

import { useReactMediaRecorder } from "react-media-recorder";



export default function SimpleCard() {
  const classes = useStyles();

  const media = useReactMediaRecorder({ audio: true, onStop: (a, b) => { blobToBase64(b);} })
  const [text, setText] = useState('')
  const [recBtn, setRecBtn] = useState(true)

  function blobToBase64(blob) {
    console.log('Connect to server...')
    var reader = new FileReader();
    reader.onload = function () {
      var dataUrl = reader.result;
      var base64 = dataUrl.split(',')[1];
      sendToOffline(base64);
    };
    reader.readAsDataURL(blob);
  };

  function sendToOffline(b64data) {
    const recipeUrl = "https://ai-demo.tmaxos.com/xwpqe/restasr/"
    const postBody = {
      'uuid': 'restasr',
      'data': b64data
    }
    const requestMetadata = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(postBody)
    };

    fetch(recipeUrl, requestMetadata).then(res => res.json()).then(res => setText(res.text))
  }

  return (
    <>
    <Card className={classes.card}>
      <NavBar classes={classes} />
      <CardContent className={classes.cardContent}>
        <Record classes={classes} recState={recBtn}/>
      </CardContent>
    </Card>

    <p>{media.status}</p>
      <button onClick={
        () => {
          recBtn ? media.startRecording() : media.stopRecording();
          setRecBtn(!recBtn)
        }
      }>{recBtn ? "Start Record" : "Stop Record"}</button>
      <p>{text}</p>
    </>
  );
}
