import React from 'react';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import MicIcon from '@material-ui/icons/Mic';

import { ListSubheader, Typography } from '@material-ui/core';

function ListItemLink({ item }) {
    return (
      <ListItem button style={{marginTop:5}}>
        <ListItemIcon>
          {item['done'] ? <MicIcon color='primary'/> : <MicIcon color='disabled'/>}
        </ListItemIcon>
        <Typography noWrap={true}>{item['text']}</Typography>
      </ListItem>
    );
  }

function DisplayList({ classes, sentList, undoneNum}) {
    return (
        <div className={classes.listContent}>
          <List component="nav">
          <ListSubheader className={classes.subheader}>Number of undone list : {undoneNum}</ListSubheader>
            {sentList.map((item, idx) => {return <ListItemLink key={idx} item={item} />})}
          </List>
        </div>
    )

}

export default DisplayList
