import React, { useState } from 'react';

import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

import useStyles from './components/styled'
import NavBar from './components/NavBar'
import Record from './components/RecordDiv'
import DisplayList from './components/DisplayList'

import { makeStyles } from '@material-ui/core/styles';
import { Button, Input } from '@material-ui/core';

const useStyles2 = makeStyles({
  content: {
    position: "relative",
    marginLeft: 100,
    marginRight: 100,
    marginTop: 150,
    textAlign:"center",
  },
  input: {
    display: "block",
    margin: "auto",
    width: "100%",
    marginBottom: "2em",
    padding: '0.5em 0',
    border: 'none',
    borderBottom: "1px solid #eaeaea",
    color: "#757575",

    '&:focus': {
      outline: "none"
    }
 }
})

export default function SimpleCard() {
  const classes = useStyles();
  const classes2 = useStyles2();

  const [sentList, setSentList] = useState([])
  const [mode, setMode] = useState('show')
  const [index, setIndex] = useState(0)

  const [user, setUser] = useState('jkp')

  function showContent() {
    if (mode === 'show') {
      if (sentList.length === 0) {
        getScript()
      }

      return (<DisplayList
        classes={classes}
        sentList={sentList}
        onClick={(id) => {
          setIndex(id)
          setMode('record')
        }} />)
    } else if (mode === 'record') {
      return (<Record
        classes={classes}
        sentList={sentList}
        setSentList={setSentList}
        index={index}
        setIndex={setIndex}
        user={user} />)
    }
  }

  function getScript() {
    const recipeUrl = "http://0.0.0.0:5024/get_text"
    const postBody = {
      'user': user
    }
    const requestMetadata = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(postBody)
    };

    fetch(recipeUrl, requestMetadata)
      .then(res => res.json())
      .then(res => {
        const tmpList = []
        res['sent_list'].map(item => {
          const sent = { id: item[0], text: item[1], done: item[2] }
          tmpList.push(sent)
        })
        setSentList(tmpList)
      })
  }


  return (
    <>
      <Card className={classes.card}>
        <NavBar classes={classes} onClick={() => { setMode('show') }} />
        <CardContent className={classes.cardContent}>
          {/* {showContent()} */}
          <div className={classes2.content}>
            <h2>Login to your account</h2>
            <form>
              <input type="text" title="username" placeholder="username" className={classes2.input} />
              <input type="password" title="username" placeholder="password" className={classes2.input} />
              <Button variant="contained" color="primary">Log in</Button>
            </form>
          </div>
        </CardContent>
      </Card>
    </>

  );
}
